INSERT INTO `dictionary` VALUES (5,'what is leftover wine?','If you have some leftover wine at the end of the evening, freeze it in ice cube trays for easy addition to soups and sauces in the future or just as ice '),(6,'Barbecue','To cook foods on a rack or a spit over coals.'),(7,'Bouquet garni','A tied bundle of herbs, usually parsley, thyme, and bay leaves, that is added to flavor soups, stews, and sauces but removed before serving.\r\n'),(8,'Caramelize','To heat sugar until it liquefies and becomes a syrup ranging in color from golden to dark brown.'),(9,'Deep-fry','To cook by completely immersing food in hot fat'),(10,'Deglaze','o loosen brown bits from a pan by adding a liquid, then heating while stirring and scraping the pan.'),(11,'Fines herbes','A mixture of herbs traditionally parsley, chervil, chives, and tarragon, used to flavor fish, chicken, and eggs.'),(12,'Grease','To rub the interior surface of a cooking dish or pan with shortening, oil, or butter to prevent food from sticking to it.'),(13,'Parboil','To partially cook by boiling. Usually done to prepare food for final cooking by another method.'),(14,'Roast','To cook a large piece of meat or poultry uncovered with dry heat in an oven.'),(15,'Steam','To cook food on a rack or in a steamer set over boiling or simmering water in a covered pan.'),(16,'Whisk','o beat ingredients (such as heavy or whipping cream, eggs, salad dressings, or sauces) with a fork or whisk to mix, blend, or incorporate air.');
